mapper.select:
SELECT
FROM flow_process_node a JOIN flow_process b ON a.process_id = b.id JOIN flow_order c ON b.id = c.proc_id JOIN t_reimburse d ON c.id = d.proc_ins_id LEFT JOIN t_cost_detail e ON d.id = e.reimburse_id LEFT JOIN flow_hi_circulation g ON g.node_id = a.id LEFT JOIN flow_hi_circulation_detailed gg ON gg.circulation_id = g.id LEFT JOIN sys_user h ON h.id = d.USER LEFT JOIN sys_office office ON h.office_id = office.id
( gg.type = '4' OR gg.type = '5' ) AND gg.user_id = #{userId} AND c.id = g.order_id
AND d.number = #{entity.number}
AND d.name LIKE CONCAT('%',#{entity.name},'%')
AND d.type = #{entity.type}
and d.allmoney > #{entity.allmoney}
and d.allmoney < #{entity.allmoney}
and d.allmoney = #{entity.allmoney}
GROUP BY d.id, h.id ORDER BY d.update_date DESC

SELECT
FROM flow_process_node a JOIN flow_process b ON a.process_id = b.id JOIN flow_order c ON b.id = c.proc_id JOIN t_reimburse d ON c.id = d.proc_ins_id LEFT JOIN t_cost_detail e ON d.id = e.reimburse_id JOIN flow_order_task g ON g.order_id = c.id LEFT JOIN sys_user h ON h.id = d.USER LEFT JOIN sys_office office ON h.office_id = office.id
FIND_IN_SET(c.id,#{orderIds}) AND g.node_id = a.id
AND d.number = #{entity.number}
AND d.name LIKE CONCAT('%',#{entity.name},'%')
AND d.type = #{entity.type}
and d.allmoney > #{entity.allmoney}
and d.allmoney < #{entity.allmoney}
and d.allmoney = #{entity.allmoney}
ORDER BY d.update_date desc

